import { api, authApi } from '../../util/api';

class Auth {
	constructor() {
	}

	async signInWithEmailAndPassword({email, password}) {
		authApi({
			method: "post",
			url: "/users/",
			data: {
				email: email,
				password: password
			}
		})
	}
}

export default Auth;
