import axios from "axios"; // const apiUrl = process.env.NODE_ENV ? process.env.DEV_API_URL : process.env.PROD_API_URL;
// const apiUrl = "http://localhost:5000/api"

var apiUrl = "http://sneakerxwars.com/api";
/* Common API */

var api = axios.create({
  baseURL: apiUrl,
  headers: {
    "Content-Type": "application/json"
  }
});
api.interceptors.response.use(function (response) {
  return response.data;
}, function (error) {
  return Promise.reject(error.response);
});
/* Auth API */

var authApi = axios.create({
  baseURL: apiUrl,
  headers: {
    "Content-Type": "application/json"
  }
});
authApi.interceptors.request.use(function (config) {
  if (!config.withoutAuth) {
    config.headers.Authorization = "token ".concat(window.localStorage.getItem("token"));
  }

  return config;
}, function (error) {
  return Promise.reject(error.response);
});
authApi.interceptors.response.use(function (response) {
  return response.data;
}, function (error) {
  return Promise.reject(error.response);
});
export { api, authApi };
