import { api, authApi } from '../../util/api';

class Auth {
    constructor() {
    }

    async createUserWithEmailPassword(user) {
        return authApi({
            method: "post",
            url: "/users/register",
            data: user
        })
    }

    async signInWithEmailAndPassword({ email, password }) {
        return authApi({
            method: "post",
            url: "/users/login",
            data: { email, password }
        })
    }
}

export default Auth;
