import { api, authApi } from '../../util/api';

class Auth {
	constructor() {
	}

	async signInWithEmailAndPassword({ email, password }) {
		return authApi({
			method: "post",
			url: "/users/login",
			data: { email, password }
		})
	}
}

export default Auth;
